#创建数据库
SET NAMES UTF8;
DROP DATABASE  IF EXISTS vt;
CREATE DATABASE vt CHARSET=UTF8;
USE vt;

#网站基本信息
CREATE TABLE vt_site_info(
	site_name VARCHAR(16),
	logo VARCHAR(64),
	copyright VARCHAR(64)
);

#导航条目
CREATE TABLE vt_navbar_item(
	name VARCHAR(16),
	url VARCHAR(64),
	title VARCHAR(32)
);

#轮播图
CREATE TABLE vt_carousel_item(
	cid INT PRIMARY KEY AUTO_INCREMENT,
	bookId INT
);

#出版图书
CREATE TABLE vt_publish(
	b1_id INT PRIMARY KEY AUTO_INCREMENT,
	bookId INT,
	sort INT
);

#出版图书导航
CREATE TABLE vt_publish_top(
	tid INT PRIMARY KEY AUTO_INCREMENT,
	label VARCHAR(8)
);


#出版编辑推荐表
CREATE TABLE vt_publish_recommend(
	rid INT PRIMARY KEY AUTO_INCREMENT,
	bookId INT,
	sort INT
);

#出版热销表
CREATE TABLE vt_publish_best(
	bid INT PRIMARY KEY AUTO_INCREMENT,
	bookId INT,
	sort INT
);

#出版新书表
CREATE TABLE vt_publish_new(
	nid INT PRIMARY KEY AUTO_INCREMENT,
	bookId INT,
	sort INT
);

#书籍分类标签表
CREATE TABLE vt_classify(
	cid INT PRIMARY KEY AUTO_INCREMENT,
	cname VARCHAR(8),         #类型分类
	label VARCHAR(8)          #标签
);

#创建用户表
CREATE TABLE vt_user(
	uid INT PRIMARY KEY AUTO_INCREMENT,
	uname VARCHAR(16),
	upwd  VARCHAR(32),
	phone VARCHAR(16),
	email VARCHAR(32),
	avatar VARCHAR(128), 
	bg  VARCHAR(128),
	readFontF SMALLINT,
	readFontS SMALLINT,
	readBg SMALLINT,
	isVIP BOOL,
	balance INT 
);

#用户头像表
CREATE TABLE vt_avatar(
	aid INT PRIMARY KEY AUTO_INCREMENT,
	pic VARCHAR(128)
);

#用户背景表
CREATE TABLE vt_bg(
	gid INT PRIMARY KEY AUTO_INCREMENT,
	pic VARCHAR(128)
);

#充值金额表
CREATE TABLE vt_money(
	yid INT PRIMARY KEY AUTO_INCREMENT,
	pay SMALLINT,
	bookCoin INT,
	give INT
);

#充值VIP表
CREATE TABLE vt_vip(
	vid INT PRIMARY KEY AUTO_INCREMENT,
	pay SMALLINT,
	buyTime VARCHAR(64),
	vipGive INT
);

#创建书评表
CREATE TABLE vt_comments(
	cid INT PRIMARY KEY AUTO_INCREMENT,
	bookId INT,
	uid INT,
	content VARCHAR(1024),
	ctime DATETIME
);

#创建书架表
CREATE TABLE vt_bookshelf(
	sid INT PRIMARY KEY AUTO_INCREMENT,
	uid INT,
	bookId INT,
	readed INT,
	isBuy BOOL
);

#小程序轮播图
CREATE TABLE vt_wx_casel(
	cid INT PRIMARY KEY AUTO_INCREMENT,
	pic VARCHAR(256)
);

#小程序排行导航(上)
CREATE TABLE vt_wx_rankTopNav(
	nid INT PRIMARY KEY AUTO_INCREMENT,
	title VARCHAR(8)
);

#小程序排行导航(左)
CREATE TABLE vt_wx_rankLeftNav(
	nid INT PRIMARY KEY AUTO_INCREMENT,
	title VARCHAR(8)	
);

#创建书籍表
CREATE TABLE vt_books(
	bid INT PRIMARY KEY AUTO_INCREMENT,
	title VARCHAR(32),
	author VARCHAR(16),    #作者
	label VARCHAR(32),     #类型
	words DECIMAL(5,1),    #字数/万
	price SMALLINT,        #价格(单位:书币)
	watch INT,             #观看人数
	pic VARCHAR(128),      #图片路径
	intro VARCHAR(1024),   #书籍介绍
	catalogs TEXT,         #书籍目录 
	texts TEXT             #书籍正文
);
